class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        row = [[] for _ in range(m)]
        for i in range(m):
            for j in range(n):
                a = board[i][j]
                if len(row[i]) < 3:
                    heappush(row[i], (a, j))
                elif row[i][0][0] < a:
                    heappop(row[i])
                    heappush(row[i], (a, j))

        ans = -inf
        for i in range(m):
            for j in range(i + 1, m):
                for k in range(j + 1, m):
                    for a, x in row[i]:
                        s = set([x])
                        cur = a
                        for b, y in row[j]:
                            if y in s: continue
                            s.add(y)
                            cur += b
                            for c, z in row[k]:
                                if z in s: continue
                                cur += c
                                if cur > ans: ans = cur
                                cur -= c
                            s.remove(y)
                            cur -= b
                        s.remove(x)
                        cur -= a
        return ans
