class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long maxSum = LLONG_MIN;

        // Store the top 3 values and their columns for each row
        vector<vector<pair<int, int>>> rowMaxes(m);

        for (int i = 0; i < m; ++i) {
            vector<pair<int, int>> values;
            for (int j = 0; j < n; ++j) {
                values.emplace_back(board[i][j], j);
            }
            // Sort by value descending
            sort(values.rbegin(), values.rend());
            // Keep only the top 3 values
            if (values.size() > 3) {
                values.resize(3);
            }
            rowMaxes[i] = values;
        }

        // Iterate over all combinations of 3 rows
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    // Try to pick one value from each of the selected rows
                    // We will use a set to track column indices to ensure they are unique
                    set<int> usedCols;
                    long long currentSum = LLONG_MIN;

                    for (const auto& val1 : rowMaxes[r1]) {
                        for (const auto& val2 : rowMaxes[r2]) {
                            if (val1.second == val2.second) continue;  // Skip if column is the same
                            for (const auto& val3 : rowMaxes[r3]) {
                                if (val1.second != val3.second && val2.second != val3.second) {
                                    currentSum = max(currentSum,
                                        (long long)val1.first + val2.first + val3.first);
                                }
                            }
                        }
                    }

                    maxSum = max(maxSum, currentSum);
                }
            }
        }

        return maxSum;
    }
};
