class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])
        Max = [[] for _ in range(m)]

        for i in range(m):
            row = [(board[i][j], j) for j in range(n)]
            row.sort(reverse=True)
            Max[i] = row[:min(3, len(row))]

        Max_Sum = float('-inf')

        for r1 in range(m):
            for r2 in range(r1 + 1, m):
                for r3 in range(r2 + 1, m):
                    for val1, c1 in Max[r1]:
                        for val2, c2 in Max[r2]:
                            if c1 == c2:
                                continue
                            for val3, c3 in Max[r3]:
                                if c3 == c1 or c3 == c2:
                                    continue
                                Curr_Sum = val1 + val2 + val3
                                Max_Sum = max(Max_Sum, Curr_Sum)

        return Max_Sum
