class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        l = []
        for i in range(m):
            for j in range(n):
                l.append((board[i][j], i, j))
        l.sort(reverse = True)
        l = l[:2 * m + 2 * n - 3]
        _, x, y = l[0]
        lx = []
        ly = []
        o = []
        for val, i, j in l:
            if i == x:
                if j != y:
                    lx.append(j)
            elif j == y:
                ly.append(i)
            else:
                o.append((val, i, j))
        _, xx, yy = o[0]
        # print(x, y)
        # print(lx)
        # print(ly)
        # print(o)
        # print(xx, yy)
        llx = lly = oo = -inf
        for val, i, j in o:
            if i == xx:
                if j != yy:
                    llx = max(llx, val)
            elif j == yy:
                lly = max(lly, val)
            else:
                oo = max(oo, val)
        mm = o[0][0] + oo
        if mm < llx + lly:
            mm = llx + lly
        ans = mm + l[0][0]
        # print(llx, lly, oo, mm, ans, l[0][0])
        for j in lx:
            for i in ly:
                for _, a, b in o:
                    if a != i and b != j:
                        break
                if ans < (tmp := board[x][j] + board[i][y] + board[a][b]):
                    ans = tmp
        return ans
