class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        long long maxTotal = LLONG_MIN;
        vector<vector<long long>> topValues(rows, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> topColumns(rows, vector<int>(3, -1));

        for (int rowIndex = 0; rowIndex < rows; ++rowIndex) {
            vector<pair<long long, int>> colPairs;
            for (int colIndex = 0; colIndex < cols; ++colIndex) {
                colPairs.emplace_back(grid[rowIndex][colIndex], colIndex);
            }
            sort(colPairs.rbegin(), colPairs.rend()); 

            for (int k = 0; k < 3 && k < cols; ++k) {
                topValues[rowIndex][k] = colPairs[k].first;
                topColumns[rowIndex][k] = colPairs[k].second;
            }
        }

        for (int firstRow = 0; firstRow < rows; ++firstRow) {
            for (int secondRow = firstRow + 1; secondRow < rows; ++secondRow) {
                for (int thirdRow = secondRow + 1; thirdRow < rows; ++thirdRow) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (topColumns[secondRow][j] == topColumns[firstRow][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (topColumns[thirdRow][k] == topColumns[firstRow][i] || topColumns[thirdRow][k] == topColumns[secondRow][j]) continue;

                                long long currentTotal = topValues[firstRow][i] + topValues[secondRow][j] + topValues[thirdRow][k];
                                maxTotal = max(maxTotal, currentTotal);
                            }
                        }
                    }
                }
            }
        }

        return maxTotal;
    }
};
