class Solution {
private:
    vector<vector<int>> data;
    bool check(vector<int> &A, vector<int> &B){
        return (A[1]!=B[1])&&(A[2]!=B[2]);
    }
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = -5000000000ll;
        for(int i=0, N=board.size() ; i<N ; i++){
            for(int j=0, M=board[0].size() ; j<M ; j++){
                data.push_back({board[i][j], i, j});
            }
        }
        sort(data.begin(), data.end());
        reverse(data.begin(), data.end());
        int size = data.size();
        int a, b;
        for(int i=0 ; i<10 ; i++){
            a = i;
            for(int c=0 ; c<10 ; c++){
                for(a=a+1 ; a<size ; a++){
                    if(check(data[i], data[a])){
                        break;
                    }
                }
                for(b=a+1 ; b<size ; b++){
                    if(check(data[i], data[b]) && check(data[a], data[b])){
                        break;
                    }
                }
                if(a<size && b<size){
                    ans = max(ans, 0ll+data[i][0]+data[a][0]+data[b][0]);
                }
            }
        }

        return ans;
    }
};
