#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<pair<ll,pair<int,int>>> v;
        for(int i=0;i<board.size();i++)
        {
            for(int j=0;j<board[0].size();j++)
            {
                v.push_back({board[i][j],{i,j}});
            }
        }
        sort(v.begin(),v.end(),greater<pair<ll,pair<int,int>>>());
        vector<pair<ll,pair<int,int>>> v1;
        for(int i=0;i<min(2*max(board[0].size(),board.size())+4,v.size());i++) v1.push_back(v[i]);
        ll ans=LLONG_MIN;
        for(int i=0;i<v1.size();i++)
        {
            for(int j=i+1;j<v1.size();j++)
            {
                if(v1[i].second.first==v1[j].second.first or v1[i].second.second==v1[j].second.second) continue;
                for(int k=j+1;k<v1.size();k++)
                {
                    if(v1[k].second.first==v1[j].second.first or v1[k].second.second==v1[j].second.second or v1[i].second.first==v1[k].second.first or v1[i].second.second==v1[k].second.second) continue;
                    ans=max(ans,v1[i].first+v1[j].first+v1[k].first);
                }
            }
        }
        return ans;
    }
};
