class Solution {
public:
    vector<pair<int, pair<int, int>>> vij;
    long long ans;
    void put(int p, int count, vector<int>& putm, vector<int>& putn, long long anstmp){
        if(count == 3){
            if(anstmp > ans){
                ans = anstmp;
            }
            return;
        }
        if(p == vij.size()){
            return;
        }

        if(count == 1){
            long long maxv = anstmp;
            if(p == vij.size()-1){
                return;
            }else{
                maxv += vij[p].first + vij[p+1].first;
                if(maxv < ans){
                    return;
                }
            }
        }
        if(count == 2){
            long long maxv = anstmp;
            maxv += vij[p].first;
            if(maxv < ans){
                return;
            }
        }

        put(p+1, count, putm, putn, anstmp);
        while(true){
            if(p == vij.size()){
                return;
            }
            int m = vij[p].second.first;
            int n = vij[p].second.second;
            bool check = false;
            for(int i=0;i<count;i++){
                if(putm[i] == m || putn[i] == n){
                    check = true;
                }
            }
            if(check){
                p++;
                continue;
            }else{
                putm[count] = m;
                putn[count] = n;
                put(p+1, count+1, putm, putn, anstmp+vij[p].first);
                break;
            }
        }
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vij = vector<pair<int, pair<int, int>>>(m*n);
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                vij[i*n+j] = {board[i][j], {i, j}};
            }
        }
        sort(vij.begin(), vij.end(), [](pair<int, pair<int, int>>& a, pair<int, pair<int, int>>& b){
            return a.first > b.first;
        });
        vector<int> putm(3);
        vector<int> putn(3);
        ans = -9999999999;
        put(0, 0, putm, putn, 0);
        return ans;


    }
};
