class Solution {
    fun maximumValueSum(board: Array<IntArray>): Long {
        val pq = PriorityQueue<IntArray>(){a1, a2 -> a1[0] - a2[0]}
        val m = board.size
        val n = board[0].size
        for(i in 0..m-1) {
            val arr = board[i]
            for(j in 0..n-1) {
                val v = arr[j]
                pq.add(intArrayOf(v, i, j))
                if(pq.size > 1000) {
                    pq.poll()
                }
            }
        }
        val size = pq.size
        val arr = Array<IntArray>(size){pq.poll()}

        var ans = Long.MIN_VALUE
        var i = size - 1
        var j: Int
        var k: Int
        var firstK = 0
        first@while(i >= 0){
            val v1 = arr[i][0].toLong()
            val x1 = arr[i][1]
            val y1 = arr[i][2]
            j = i - 1
            second@while(j > firstK) {
                val v2 = arr[j][0]
                val x2 = arr[j][1]
                val y2 = arr[j][2]
                if(x1 == x2 || y1 == y2) {
                    j--
                    continue
                }
                k = j - 1
                while(k >= 0) {
                    val v3 = arr[k][0]
                    val x3 = arr[k][1]
                    val y3 = arr[k][2]
                    if(x1 == x3 || x2 == x3 || y1 == y3 || y2 == y3) {
                        k--
                        continue
                    }
                    val cur = v1 + v2 + v3
                    ans = maxOf(ans, cur)
                    firstK = k
                    j--
                    continue@second
                }
                j--
            }
            i--
        }
        return ans
    }
}
