#define lli long long
class Solution {
public:
    lli fuc(lli row,lli col1,lli col2,vector<vector<vector<lli>>>&dp,vector<vector<pair<lli,lli>>>&v1){
        lli n=v1.size();
        if(row==n+1){
            return -1e15;
        }
        if(dp[row][col1][col2]!=-1){
            return dp[row][col1][col2];
        }
        lli ans=fuc(row+1,col1,col2,dp,v1);
        for(auto it1:v1[row-1]){
                if(col2==0&&col1==0){
                    ans=max(ans,it1.first+fuc(row+1,it1.second+1,col2,dp,v1));
                }
                else if(it1.second+1!=col1&&col2==0){
                    ans=max(ans,it1.first+fuc(row+1,col1,it1.second+1,dp,v1));
                }
                else if(col1!=0&&col2!=0&&it1.second+1!=col1&&it1.second+1!=col2){
                    ans=max(ans,it1.first);
                }
        }
        return dp[row][col1][col2]=ans;
    }
    long long maximumValueSum(vector<vector<int>>& v) {
        lli n=v.size();
        lli m=v[0].size();
        vector<vector<pair<lli,lli>>>v1(n,vector<pair<lli,lli>>(3));
        for(lli i=0;i<n;i++){
            //cout<<i<<endl;
            vector<pair<lli,lli>>vp;
            for(lli j=0;j<m;j++){
                vp.push_back({v[i][j],j});
            }
            sort(vp.begin(),vp.end());
            //cout<<vp.size()<<endl;
            for(lli j=vp.size()-1;j>=vp.size()-3;j--){
                //cout<<j<<endl;
                if(j<0){
                    break;
                }
                v1[i][vp.size()-1-j]={vp[j].first,vp[j].second};
            }
        }
        //cout<<"hi\n";
        vector<vector<vector<lli>>>dp(n+1,vector<vector<lli>>(m+1,vector<lli>(m+1,-1)));
        return fuc(1,0,0,dp,v1);
    }
};
