#define ll long long
#define ff first
#define ss second
class Solution {
public:

    void getMax(int i,vector<vector<int>>& v,vector<vector<pair<ll,ll>>>&mp){
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;
        pq.push({v[i][0],0});
        pq.push({v[i][1],1});
        pq.push({v[i][2],2});
        int m=v[0].size();
        for(int j=3;j<m;j++){
            pq.push({v[i][j],j});
            pq.pop();
        }
        while(pq.size()){
            mp[i].push_back(pq.top());
            pq.pop();
        }
        return;

    }

    long long maximumValueSum(vector<vector<int>>& v) {
        int n=v.size(),m=v[0].size();
        vector<vector<pair<ll,ll>>>mp(n+1);
        for(int i=0;i<n;i++){
            getMax(i,v,mp);
        }
        ll ans=-1e12;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){

                    for(int a=0;a<3;a++){
                        for(int b=0;b<3;b++){
                            for(int c=0;c<3;c++){
                                if(mp[i][a].ss!=mp[j][b].ss and mp[i][a].ss!=mp[k][c].ss and mp[j][b].ss!=mp[k][c].ss){
                                    ans=max(ans,mp[i][a].ff+mp[j][b].ff+mp[k][c].ff);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
