class Solution {
public:
int m;
int n;
    long long max(long long a, long long b) {
        return a > b ? a:b;
    }
    long long solve_for_1(vector<vector<int>>& board, int row1, int col1, int row2, int col2) {
        long long ans = INT_MIN;
        for (int i=0;i<m;i++)
        {
            if (i == row1 || i == row2)
                continue;
            for (int j=0;j<n;j++)
            {
                if (j == col1 || j == col2)
                    continue;

                ans = max(ans, board[i][j]);
            }
        }

        return ans;
    }
    long long solve_for_2(vector<vector<int>>& board, int row, int col) {
        vector<pair<int,int>> maxes(2,pair(-1,-1));
        vector<long long> vals(2,INT_MIN);

        for (int i=0;i<m;i++)
        {
            if (i == row)
                continue;
            for (int j=0;j<n;j++)
            {
                if (j == col)
                    continue;
                int val = board[i][j];
                pair<int,int> curr = pair(i,j);
                for (int k=0;k<2;k++)
                {
                    if (val > vals[k])
                    {
                        int temp = val;
                        pair<int,int> temp2 = curr;

                        val = vals[k];
                        curr = maxes[k];

                        vals[k] = temp;
                        maxes[k] = temp2;

                    }
                }
            }
        }

        long long ans = -1e12;

        for (int i=0;i<2;i++)
            ans = max(ans, vals[i] + solve_for_1(board, row, col, maxes[i].first, maxes[i].second));

        return ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        m = board.size();
        n = board[0].size();
        vector<pair<int,int>> maxes(6,pair(-1,-1));
        vector<int> vals(6,INT_MIN);

        for (int i=0;i<m;i++)
        {
            for (int j=0;j<n;j++)
            {
                int val = board[i][j];
                pair<int,int> curr = pair(i,j);
                for (int k=0;k<6;k++)
                {
                    if (val > vals[k])
                    {
                        int temp = val;
                        pair<int,int> temp2 = curr;

                        val = vals[k];
                        curr = maxes[k];

                        vals[k] = temp;
                        maxes[k] = temp2;
                    }
                }
            }
        }

        long long ans = -1e12;

        for (int i=0;i<6;i++)
            ans = max(ans, vals[i]+solve_for_2(board, maxes[i].first, maxes[i].second));

        return ans;
    }
};
