class Solution {
public:
    int row, col;
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = -10000000000;
        row = board.size(), col = board[0].size(); 
        vector<set<pair<int, int>>> store(row);
        vector<vector<pair<int, int>>> store2(row);
        for(int i=0;i<row;i++){
            for(int j=0;j<col;j++){
                store[i].insert({board[i][j], j}); 
                if(j>=3) store[i].erase(store[i].begin());
            }
            for(auto x : store[i]){
                //cout<<x.first<<" "<<x.second<<" + ";
                store2[i].push_back(x);  
            }
            //cout<<endl; 
        }
        for(int r1=0; r1<row;r1++){
            for(int r2=r1+1;r2<row;r2++){
                for(int r3=r2+1;r3<row;r3++){
                    for(int i=0;i<3;i++){
                        for(int j=0;j<3;j++){
                            for(int k=0;k<3;k++){
                                long long e1 = store2[r1][i].first;
                                int id1 = store2[r1][i].second;
                                long long e2 = store2[r2][j].first;
                                int id2 = store2[r2][j].second;
                                long long e3 = store2[r3][k].first;
                                int id3 = store2[r3][k].second;
                                if((id1==id2)||(id2==id3)||(id1==id3)) continue; 
                                //cout<<e1<<" "<<e2<<" "<<e3<<endl;
                                long long res = e1+e2+e3;
                                if(res > ans )
                                    ans = res;  

                            }
                        }
                    }
                }
            }
        }

        return ans; 
    }
};
