class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size();
        vector <vector<long long>> pre(n+2,vector<long long>(m+2,-1e10));
        for (int j=1;j<=m;j++){
            for (int i=1;i<=n;i++){
                pre[i][j]=max(pre[i-1][j],1LL*a[i-1][j-1]);
            }
        }
        vector <vector<long long>> sf(n+2,vector<long long>(m+2,-1e10));
        for (int j=m;j>=1;j--){
            for (int i=n;i>=1;i--){
                sf[i][j]=max(sf[i+1][j],1LL*a[i-1][j-1]);
            }
        }
        vector <array<long long,2>> mt1[n+1],mt2[n+1];
        for (int i=1;i<=n;i++){
            for (int j=1;j<=m;j++){
                mt1[i].push_back({pre[i][j],j});
                mt2[i].push_back({sf[i][j],j});
            }
            sort(mt1[i].begin(),mt1[i].end());
            sort(mt2[i].begin(),mt2[i].end());
        }

        long long ans=-1e10;
        for (int i=2;i<=n-1;i++){
            for (int j=1;j<=m;j++){
                for (int x=m-1;x>=m-3;x--){
                    for (int y=m-1;y>=m-3;y--){
                        if (mt1[i-1][x][1]!=j && mt2[i+1][y][1]!=j && mt1[i-1][x][1]!=mt2[i+1][y][1]){
                            long long curr=a[i-1][j-1]+mt1[i-1][x][0]+mt2[i+1][y][0];
                            ans=max(ans,curr);
                            //if (ans==134){
                               // cout<<i<<" "<<j<<" "<<mt1[i-1][x][1]<<" "<<mt2[i+1][y][1]<<" "<<mt1[i-1][x][0]<<" "<<mt2[i+1][y][0]<<"\n";
                            //}
                        }
                    }
                }
            }
        }
        return ans;
    }
};
