class Solution {
public:
  long long maximumValueSum(vector<vector<int>>& A) {
    int n = A.size();
    int m = A[0].size();
    vector<vector<pair<int64_t, int>>> B(n);
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < m; j++) B[i].push_back({A[i][j], j});
      sort(B[i].begin(), B[i].end(), greater<>());
      B[i].erase(B[i].begin()+3, B[i].end());
    }
    int64_t ans = -0x3f3f3f3f3f3f3f3fll;
    for (int i = 0; i < n; i++) {
      for (int j = i+1; j < n; j++) {
        for (int k = j+1; k < n; k++) {
          for (auto &[x, i1]: B[i]) {
            for (auto &[y, i2]: B[j]) {
              for (auto &[z, i3]: B[k]) if (i1 != i2 && i2 != i3 && i3 != i1) ans = max(ans, x+y+z);
            }
          }
        }
      }
    }
    return ans;
  }
};
