vector<int> extractTopThreeIndices(vector<int>& arr) {
    vector<pair<int, int>> indexedArray;
    for (int i = 0; i < arr.size(); i++) {
        indexedArray.push_back({arr[i], i});
    }
    sort(indexedArray.begin(), indexedArray.end());
    vector<int> topIndices;
    for (int i = 0; i < 3; i++) {
        int lastIndex = arr.size() - 1 - i;
        topIndices.push_back(indexedArray[lastIndex].second);
    }
    return topIndices;
}

vector<pair<int, int>> extractTopThreePairs(vector<int>& arr) {
    vector<pair<int, int>> indexedArray;
    for (int i = 0; i < arr.size(); i++) {
        indexedArray.push_back({arr[i], i});
    }
    sort(indexedArray.begin(), indexedArray.end());

    vector<pair<int, int>> topPairs;
    for (int i = 0; i < 3; i++) {
        int lastIndex = arr.size() - 1 - i;
        topPairs.push_back({indexedArray[lastIndex].second, indexedArray[lastIndex].first});
    }

    return topPairs;
}


long long calculateMaxSum(vector<int> a, vector<int> b, vector<pair<int, int>> c,
                          int r1, int r2, vector<vector<int>>& board) {
    long long maxSum = LLONG_MIN;
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            for (int k = 0; k < 3; k++) {
                if (a[i] != b[j] && b[j] != c[k].first && a[i] != c[k].first) {
                    long long sum1 = board[r1][a[i]];
                    long long sum2 = board[r2][b[j]];
                    long long sum3 = c[k].second;
                    maxSum = max(maxSum, sum1 + sum2 + sum3);
                }
            }
        }
    }
    return maxSum;
}



class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
    int numRows = a.size();
    int numCols = a[0].size();

    vector<vector<int>> topIndicesPerRow(numRows);
    vector<vector<pair<int, int>>> topPairsPerRow(numRows);
    for (int i = 0; i < numRows; i++) {
        topIndicesPerRow[i] = extractTopThreeIndices(a[i]);
    }

    vector<int> maxValues(numCols, -1e9 - 1);
    for (int i = numRows - 1; i >= 2; i--) {
        for (int j = 0; j < numCols; j++) {
            maxValues[j] = max(maxValues[j], a[i][j]);
        }
        topPairsPerRow[i] = extractTopThreePairs(maxValues);
    }
    long long maxSum = -3e9 - 1;
    for (int i = 0; i < numRows; i++) {
        for (int j = i + 1; j < numRows; j++) {
            if (j + 1 <= numRows - 1) {
                maxSum = max(maxSum, calculateMaxSum(topIndicesPerRow[i], topIndicesPerRow[j], topPairsPerRow[j + 1], i, j, a));
            }
        }
    }

    return maxSum;
}

};
