class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n, m = len(board), len(board[0])
        mx = [[] * 3 for _ in range(n)]
        for i, r in enumerate(board):
            cur = sorted([(x, idx) for idx, x in enumerate(r)], key=lambda x:-x[0])
            mx[i] = cur[:3]

        def cal(a, b, c):
            ans = -inf
            for i in range(3):
                for j in range(3):
                    for k in range(3):
                        if a[i][1] == b[j][1] or a[i][1] == c[k][1] or b[j][1] == c[k][1]:
                            continue
                        ans = max(ans, a[i][0]+b[j][0]+c[k][0])
            return ans

        ans = -inf
        for i in range(n):
            for j in range(i+1, n):
                for k in range(j+1, n):
                    ans = max(ans, cal(mx[i], mx[j], mx[k]))
        return ans
