class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(), m=board[0].size();
        vector <vector <pair <int, int>>> v(n);
        for (int i=0; i<n; i++)
        {
            for (int j=0; j<m; j++)
            {
                v[i].push_back({board[i][j], j});
            }
            sort (v[i].rbegin(), v[i].rend());
        }
        long long answer=-LLONG_MAX;
        for (int i=0; i<n; i++)
        {
            for (int j=i+1; j<n; j++)
            {
                for (int k=j+1; k<n; k++)
                {
                    if (answer<(long long)v[i][0].first+(long long)v[j][0].first+(long long)v[k][0].first)
                    {
                        for (int p=0; p<3; p++)
                        {
                            for (int q=0; q<3; q++)
                            {
                                for (int r=0; r<3; r++)
                                {
                                    set <int> s;
                                    s.insert(v[i][p].second);
                                    s.insert(v[j][q].second);
                                    s.insert(v[k][r].second);
                                    if (s.size()==3)
                                    {
                                        answer=max(answer, (long long)v[i][p].first+(long long)v[j][q].first+(long long)v[k][r].first);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
        return answer;
    }
};
