class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int n = b.size(), m = b[0].size();
        vector<vector<vector<long long>>> a(n);
        for (int i = 0; i < n; i++) {
            vector<vector<long long>> t;
            for (int j = 0; j < m; j++) {
                t.push_back({b[i][j], j});
            }
            sort(t.begin(), t.end());
            for (int j = m - 3; j <= m - 1; j++) {
                a[i].push_back(t[j]);
            }
        }
        long long res = -1e18;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int k = j + 1; k < n; k++) {
                    for (int i1 = 0; i1 < 3; i1++) {
                        for (int i2 = 0; i2 < 3; i2++) {
                            for (int i3 = 0; i3 < 3; i3++) {
                                if (a[i][i1][1] != a[j][i2][1] && a[i][i1][1] != a[k][i3][1] && a[j][i2][1] != a[k][i3][1]) {
                                    res = max(res, a[i][i1][0] + a[j][i2][0] + a[k][i3][0]);
                                }
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
