class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])
        maxv = [[-1000_000_001]*3 for _ in range(m)]
        idx = [[-1]*3 for _ in range(m)]
        for i in range(m):
            for j in range(n):
                if board[i][j] > maxv[i][0]:
                    maxv[i][0], maxv[i][1], maxv[i][2] = board[i][j], maxv[i][0], maxv[i][1]
                    idx[i][0], idx[i][1], idx[i][2] = j, idx[i][0], idx[i][1]
                elif board[i][j] > maxv[i][1]:
                    maxv[i][1], maxv[i][2] = board[i][j], maxv[i][1]
                    idx[i][1], idx[i][2] = j, idx[i][1]
                elif board[i][j] > maxv[i][2]:
                    maxv[i][2] = board[i][j]
                    idx[i][2] = j

        res = -3000_000_001
        for i in range(m-2):
            for j in range(i+1, m-1):
                for k in range(j+1, m):
                    for a in range(3):
                        for b in range(3):
                            for c in range(3):
                                if idx[i][a] != idx[j][b] and idx[j][b] != idx[k][c] and idx[i][a] != idx[k][c]:
                                    res = max(res, maxv[i][a] + maxv[j][b] + maxv[k][c])
        return res
