class Solution {
public:
    struct node
    {
        int x, y, num;
        bool operator < (const node &a)const
        {
            return num > a.num;
        }
    };

    long long maximumValueSum(vector<vector<int>>& board) {

        int n = board.size();
        int m = board[0].size();
        vector<node> vt;

        for(int i = 0; i < n; ++i)
        {
            for(int j = 0; j < m; ++j)
            {
                vt.push_back({i, j, board[i][j]});
            }
        }

        sort(vt.begin(), vt.end());

        int len = min(305, (int)vt.size());
        long long ans = -3e9;

        for(int i = 0; i < len; ++i)
        {
            for(int j = i + 1; j < len; ++j)
            {
                for(int k = j + 1; k < len; ++k)
                {
                    if(vt[i].x != vt[j].x && vt[i].x != vt[k].x && vt[j].x != vt[k].x
                            &&vt[i].y != vt[j].y && vt[i].y != vt[k].y && vt[j].y != vt[k].y)
                    {
                        long long sum = (long long)vt[i].num + (long long)vt[j].num + (long long)vt[k].num;
                        ans = max(ans, sum);

                    }
                }
            }
        }

        return ans;
    }
};
