#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {

        int n = board.size(),m = board[0].size();


        int i,j,k;
        vector<vector<pair<ll,ll>>> cols(n);

        for(i=0;i<n;i++) {
            for(j=0;j<m;j++) {
                cols[i].push_back({board[i][j],j});
            }

            sort(cols[i].begin(),cols[i].end(),greater<pair<ll,ll>>());
        }

        ll res = -1e16;
        int x,y,z;
        for(i=0;i<n;i++) {
            for(j=i+1;j<n;j++) {
                for(k=j+1;k<n;k++) {

                  for(x=0;x<3;x++) {
                      for(y=0;y<3;y++) {
                          for(z=0;z<3;z++) {

                              if(cols[i][x].second != cols[j][y].second 
                                && cols[j][y].second != cols[k][z].second
                                && cols[i][x].second != cols[k][z].second) {

                                  res = max(res,cols[i][x].first + cols[j][y].first + cols[k][z].first);

                              }


                          }
                      }
                  }  



                }
            }
        }

        return res;

    }
};
