class Solution {
public:
    long long maximumValueSum(std::vector<std::vector<int>>& board) {
        int rows = board.size();
        int cols = board[0].size();
        long long maxValueSum = LLONG_MIN;

        auto topThreeValues = getTopThreeValues(board, rows, cols);

        for (int r1 = 0; r1 < rows; ++r1) {
            for (int r2 = r1 + 1; r2 < rows; ++r2) {
                for (int r3 = r2 + 1; r3 < rows; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (topThreeValues[r1][i].second == topThreeValues[r2][j].second) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (topThreeValues[r1][i].second == topThreeValues[r3][k].second || topThreeValues[r2][j].second == topThreeValues[r3][k].second) continue;
                                long long currentSum = static_cast<long long>(topThreeValues[r1][i].first) +
                                                       static_cast<long long>(topThreeValues[r2][j].first) +
                                                       static_cast<long long>(topThreeValues[r3][k].first);
                                maxValueSum = std::max(maxValueSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxValueSum;
    }

private:
    std::vector<std::array<std::pair<int, int>, 3>> getTopThreeValues(std::vector<std::vector<int>>& board, int rows, int cols) {
        std::vector<std::array<std::pair<int, int>, 3>> topValues(rows);
        for (int i = 0; i < rows; ++i) {
            std::vector<std::pair<int, int>> rowPairs;
            for (int j = 0; j < cols; ++j) {
                rowPairs.push_back({board[i][j], j});
            }
            std::sort(rowPairs.rbegin(), rowPairs.rend());
            for (int k = 0; k < 3; ++k) {
                topValues[i][k] = rowPairs[k];
            }
        }
        return topValues;
    }
};
