#define ll long long
#define pii pair<int,int>
class Solution {
public:
    long long findMax(int i,int j,int k,vector<vector<pair<int,int>>> &vp){
        long long maxi = -1e17; 
        for(int a = 0; a < 3; a++){
            for(int b = 0; b < 3; b++){
                for(int c = 0; c < 3; c++){
                    pii p1 = vp[i][a], p2 = vp[j][b], p3 = vp[k][c]; 
                    if(p1.second != p2.second && p2.second != p3.second && p1.second != p3.second){

                        maxi = max(maxi , (ll)p1.first + (ll)p2.first + (ll)p3.first); 
                    }
                }
            }
        }
        return maxi; 
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(); 
        int m = board[0].size(); 
        vector<vector<pair<int,int>>> vp(n,vector<pair<int,int>>(3)); 
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<>> pq; 
        for(int i = 0; i < n; i++){

            for(int j = 0; j< m;j++){
                if(pq.size() < 3)pq.push({board[i][j],j}); 
                else if(pq.top().first < board[i][j]){
                    pq.pop(); 
                    pq.push({board[i][j],j}); 
                }
            }
            vp[i][0] = pq.top(); pq.pop(); 
            vp[i][1] = pq.top(); pq.pop();
            vp[i][2] = pq.top(); pq.pop(); 
            sort(vp[i].begin(),vp[i].end(),greater<>()); 
        }
        // now we have max in every row, 
        // now pic 3 rows 
        long long maxi = -1e17; 
        for(int i = 0; i < n;i++){
            for(int j = i+1; j < n;j++){
                for(int k = j+1; k < n;k++){
                    // these are the three rows where we will place rooks
                    maxi = max(maxi , findMax(i,j,k,vp)); 
                }
            }
        }
        return maxi; 
    }
};
