class Solution {
 public:
  long long maximumValueSum(vector<vector<int>>& board) {    
    int R = board.size(), C = board[0].size();

    vector<vector<int>> max_up(R, vector<int>(C));
    for (int r = 0; r < R; ++r) {
      for (int c = 0; c < C; ++c) {
        if (r == 0) {
          max_up[r][c] = board[r][c];
        } else {
          max_up[r][c] = max(max_up[r - 1][c], board[r][c]);
        }
      }
    }

    vector<vector<int>> max_down(R, vector<int>(C));
    for (int r = R - 1; r >= 0; --r) {
      for (int c = 0; c < C; ++c) {
        if (r == R - 1) {
          max_down[r][c] = board[r][c];
        } else {
          max_down[r][c] = max(max_down[r + 1][c], board[r][c]);
        }
      }
    }

    long long max_sum = -4E9;
    // enumerate center location
    for (int r = 1; r + 1 < R; ++r) {
      multiset<int> all_down;
      for (int c = 0; c < C; ++c) {
        all_down.insert(max_down[r + 1][c]);
      }
      for (int c = 0; c < C; ++c) {
        all_down.erase(all_down.find(max_down[r + 1][c]));

        for (int c2 = 0; c2 < C; ++c2) {
          if (c2 == c) {
            continue;
          }
          all_down.erase(all_down.find(max_down[r + 1][c2]));

          long long sum = static_cast<long long>(board[r][c]) + max_up[r - 1][c2] + *(--all_down.end());
          max_sum = max(sum, max_sum);

          all_down.insert(max_down[r + 1][c2]);
        }

        all_down.insert(max_down[r + 1][c]);
      }
    }
    return max_sum;
  }
};
