long long dp[102][101][101];
// long long gg[102][101][101];
const long long mini=-1e18;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();
        for(int i=0;i<=m;i++){
            for(int j=0;j<n;j++){
                for(int k=j+1;k<n;k++){
                    dp[i][j][k]=mini;
                }
            }
        }
        for(int i=m-1;i>=0;i--){
            multiset<int> s={board[i].begin(),board[i].end()};
            for(int j=0;j<n;j++){
                s.erase(s.find(board[i][j]));
                for(int k=j+1;k<n;k++){
                    s.erase(s.find(board[i][k]));
                    dp[i][j][k]=max(dp[i+1][j][k],0LL+*s.rbegin());
                    s.insert(board[i][k]);
                }
                s.insert(board[i][j]);
            }
        }
        vector<pair<int,int>> arr[m];
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                arr[i].push_back({board[i][j],j});
            }
        }
        for(auto &i:arr){
            sort(i.rbegin(),i.rend());
        }
        int k=min(n,5);
        long long res=mini;
        for(int i=0;i<m-2;i++){
            for(int j=i+1;j<m-1;j++){
                for(int x=0;x<k;x++){
                    for(int y=0;y<k;y++){
                        if(arr[i][x].second==arr[j][y].second)continue ;
                        int a=arr[i][x].second;
                        int b=arr[j][y].second;
                        if(a>b)swap(a,b);
                        res=max(res,0LL+arr[i][x].first+arr[j][y].first+dp[j+1][a][b]);

                    }
                }
            }
        }
        return res;
    }
};
