#define pii pair<int, int>
#define f first
#define s second

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        long long ans = INT64_MIN;
        vector<vector<int>> perms = {};
        for(int i=0; i<3; i++) {
            for(int j=0; j<3; j++) {
                for(int k=0; k<3; k++) {
                    perms.push_back({i, j, k});
                }
            }
        }

        vector<vector<pii>> a(n, vector<pii>{});
        for(int i=0; i<n; i++) {
            pii p;
            for(int j=0; j<m; j++) {
                p.f = board[i][j];
                p.s = j;
                a[i].push_back(p);
            }
            sort(a[i].rbegin(), a[i].rend());
        }

        for(int i=0; i<n; i++) {
            for(int j=i+1; j<n; j++) {
                for(int k=j+1; k<n; k++) {
                    for(auto& p: perms) {
                        if (a[i][p[0]].s != a[j][p[1]].s && a[i][p[0]].s != a[k][p[2]].s && a[j][p[1]].s != a[k][p[2]].s) {
                            ans = max(ans, 1LL * a[i][p[0]].f + 1LL * a[j][p[1]].f + 1LL * a[k][p[2]].f);
                        }
                    }
                }
            }
        }

        return ans;
    }
};
