class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        const int m = board.size();
        const int n = board.front().size();
        vector<array<pair<int, int>, 3>> maxPerRow(m);
        for (int r = 0; r < m; r++) {
            const auto &row = board[r];
            vector<pair<int, int>> sortedRow; sortedRow.reserve(n);
            for (int c = 0; c < n; c++) {
                sortedRow.push_back(make_pair(row[c], c));
            }
            sort(sortedRow.begin(), sortedRow.end());
            for (int i = 0; i < 3; i++) {
                maxPerRow[r][i] = sortedRow[n - 1 - i];
            }
        }
        long long res = -1000000000000LL;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < i; j++) {
                for (int k = 0; k < j; k++) {
                    res = max(res, getMaxValue(maxPerRow[i], maxPerRow[j], maxPerRow[k]));
                }
            }
        }
        return res;
    }

private:
    long long getMaxValue(const array<pair<int, int>, 3> &a1, const array<pair<int, int>, 3> &a2, const array<pair<int, int>, 3> &a3) {
        long long maxValue = -1000000000000LL;
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                for (int k = 0; k < 3; k++) {
                    int index1 = a1[i].second;
                    int index2 = a2[j].second;
                    int index3 = a3[k].second;
                    if (index1 == index2 || index2 == index3 || index3 == index1) {
                        continue;
                    }
                    long long value1 = a1[i].first;
                    long long value2 = a2[j].first;
                    long long value3 = a3[k].first;
                    maxValue = max(maxValue, value1 + value2 + value3);
                }
            }
        }
        return maxValue;
    }
};
