class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e10;
        vector<vector<pair<long long,int>>> v(board.size(),vector<pair<long long,int>>(board[0].size()));
        for(int i=0;i<board.size();++i){
            for(int j=0;j<board[0].size();++j){
                v[i][j]={board[i][j],j};
            }
        }
        for(int i=0;i<board.size();++i){
            sort(v[i].begin(),v[i].end(),greater());
        }
        for(int i=0;i<board.size()-2;++i){
            for(int j=i+1;j<board.size()-1;++j){
                for(int k=j+1;k<board.size();++k){
                    for(int a=0;a<3;++a){
                        for(int b=0;b<3;++b){
                            for(int c=0;c<3;++c){
                                if((v[i][a].second!=v[j][b].second)&&(v[i][a].second!=v[k][c].second)&&(v[j][b].second!=v[k][c].second)) {
                                    ans=max(ans,v[i][a].first+v[j][b].first+v[k][c].first);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
