class Solution {
public:
#include <algorithm>

    long long get(vector<pair<int,int>> &v1,vector<pair<int,int>> &v2,vector<pair<int,int>> &v3){
        long long maxn = -1e18;
        for(int i = 0;i < 3;i++){
            for(int j = 0;j < 3;j++){
                for(int k = 0;k < 3;k++){
                    if(v1[i].second != v2[j].second && v1[i].second != v3[k].second && v2[j].second != v3[k].second){
                        maxn = max(maxn,v1[i].first * 1LL + v2[j].first + v3[k].first);
                    }
                }
            }
        }
        return maxn;

    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int,int>>> tmp(n,vector<pair<int,int>>(m));
        for(int i = 0;i < n;i++){
            for(int j = 0;j < m;j++){
                tmp[i][j] = {board[i][j],j};
            }
            sort(tmp[i].begin(),tmp[i].end(),greater<>());
        }
        long long ans = -1e18;
        for(int i = 0;i < n;i++){
            for(int j = 0;j < n;j++){
                for(int k = 0;k < n;k++){
                    if(i == j || j == k || i == k) continue;
                    ans = max(ans,get(tmp[i],tmp[j],tmp[k]));
                }
            }
        }
        return ans;
    }
};
