class Solution {
	class Elem {
		int r, c;
		long val;

		@Override
		public String toString() {
			return "Elem{" +
					"r=" + r +
					", c=" + c +
					", val=" + val +
					'}';
		}
	}

	int height, width;
	Elem rows[][];
//	Elem cols[][];
	public long maximumValueSum(int[][] board) {
		height = board.length;
		width = board[0].length;
		rows = new Elem[height][3];
//		cols = new Elem[width][3];

		for (int r = 0 ; r < height ; r++) {
			rows[r] = new Elem[3];
			PriorityQueue<Elem> q = new PriorityQueue<>((a, b) -> Long.compare(a.val, b.val));
			for (int c = 0 ; c < width ; c++) {
				Elem newElem = new Elem();
				newElem.r = r;
				newElem.c = c;
				newElem.val = board[r][c];
				q.add(newElem);

				while (q.size() > 3) {
					q.poll();
				}
			}

			rows[r][2] = q.poll();
			rows[r][1] = q.poll();
			rows[r][0] = q.poll();
		}

//		for (int c = 0 ; c < width ; c++) {
//			cols[c] = new Elem[3];
//			PriorityQueue<Elem> q = new PriorityQueue<>((a, b) -> Long.compare(a.val, b.val));
//			for (int r = 0 ; r < height ; r++) {
//				Elem newElem = new Elem();
