//			}
//
//			cols[c][2] = q.poll();
//			cols[c][1] = q.poll();
//			cols[c][0] = q.poll();
//		}

		Long maxNum = null;
		for (int r0 = 0 ; r0 < height ; r0++) {
			int c0 = rows[r0][0].c;
			for (int r1 = 0 ; r1 < height ; r1++) {
				if (r0 == r1) continue;
				int c1;
				c1 = rows[r1][0].c;
				if (c0 == c1) {
					c1 = rows[r1][1].c;
				}

				for (int r2 = 0; r2 < height ; r2++) {
					if (r0 == r2) continue;
					if (r1 == r2) continue;
					int c2;
					c2 = rows[r2][0].c;
					if ((c0 == c2) || (c1 == c2)) {
						c2 = rows[r2][1].c;
						if ((c0 == c2) || (c1 == c2)) {
							c2 = rows[r2][2].c;
						}
					}

					long curDist = board[r0][c0];
					curDist = curDist + board[r1][c1];
					curDist = curDist + board[r2][c2];

					if ((maxNum == null) || (maxNum < curDist)) {
						maxNum = curDist;
					}

				}
			}
		}

		return maxNum;
	}
}
