class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size();
        vector<vector<pair<int,int>>>dp(m);
        for(int j=0;j<m;j++){
            vector<pair<int,int>>vec;
            for(int i=0;i<n;i++){
                vec.push_back({a[i][j],i});
            }
            sort(vec.begin(),vec.end());
            dp[j].push_back(vec[n-1]);
            dp[j].push_back(vec[n-2]);
            dp[j].push_back(vec[n-3]);
        }
        long long ans= -3000000000;
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                for(int k=j+1;k<m;k++){
                for(int x=0;x<3;x++){
                    for(int y=0;y<3;y++){
                        for(int z=0;z<3;z++){
                            if(dp[i][x].second !=dp[j][y].second && dp[j][y].second != dp[k][z].second && dp[i][x].second != dp[k][z].second){
                               long long temp1=dp[i][x].first;
                               long long temp2=dp[j][y].first;
                               long long temp3=dp[k][z].first;
                               ans=max(ans,temp1+temp2+temp3);
                            }
                        }
                    }
                } 


                }
            }
        }
   return ans;
    }
};
