class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        int n=v.size(),m=v[0].size();
        vector<vector<int>>dp(n,vector<int>(m,-2e9));
        vector<vector<int>>dp2(n,vector<int>(m,-2e9));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                dp[i][j]=v[i][j];
                if(i)dp[i][j]=max(dp[i][j],dp[i-1][j]);
            }
        }
        for(int i=n-1;i>=0;i--){
            for(int j=m-1;j>=0;j--){
                dp2[i][j]=v[i][j];
                if(i!=n-1)dp2[i][j]=max(dp2[i][j],dp2[i+1][j]);
            }
        }
        long long ans=-4e9;
        for(int i=1;i+1<n;i++){
            for(int j=0;j<m;j++){
                vector<pair<int,int>>a,b;
                for(int k=0;k<m;k++){
                    if(k==j)continue;
                    a.push_back({dp[i-1][k],k});
                    b.push_back({dp2[i+1][k],k});
                }
                sort(a.rbegin(),a.rend());
                sort(b.rbegin(),b.rend());
                for(int k=0;k<min(5,(int)a.size());k++){
                    for(int l=0;l<min(5,(int)b.size());l++){
                        if(a[k].second!=b[l].second)
                        ans=max(ans,1LL*a[k].first+b[l].first+v[i][j]);
                    }
                }
            }
        }
        return ans;
    }
};
