class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        nrow = len(board)
        ncol = len(board[0])

        top3_arr = []
        for i in range(nrow):
            tmp = sorted([(v, ii) for ii, v in enumerate(board[i])])[-3:]
            top3_arr.append(tmp)

        # print(top3_arr)
        res = None
        for i in range(nrow):
            for j in range(i + 1, nrow):
                for k in range(j + 1, nrow):
                    for ii in range(3):
                        for jj in range(3):
                            if top3_arr[i][ii][1] == top3_arr[j][jj][1]:
                                continue
                            for kk in range(3):
                                if top3_arr[i][ii][1] == top3_arr[k][kk][1] or top3_arr[j][jj][1] == top3_arr[k][kk][1]:
                                    continue

                    # for ii, jj, kk in [(0, 1, 2), (0, 2, 1), (1, 0, 2), (1, 2, 0), (2, 0, 1), (2, 1, 0)]:
                                tmp = top3_arr[i][ii][0] + top3_arr[j][jj][0] + top3_arr[k][kk][0]

                                res = tmp if res is None else max(res, tmp)

        return res
