class Solution {
public:
long long maximumValueSum(vector<vector<int>> board) {
    int m = board.size(), n = board[0].size();
    vector<vector<pair<long long,long long>>> nums(m);
    for(int i = 0;i < m;i++){
        for(int j = 0;j < n;j++){
            nums[i].push_back({board[i][j],j});
        }
        sort(nums[i].begin(),nums[i].end(),greater<pair<int,int>>());
    }
    long long ans = LLONG_MIN;
    for(int i = 0;i < m;i++){
        for(int j = i + 1;j < m;j++){
            for(int k = j + 1;k < m;k++){
                for(int a = 0;a < 3;a++){
                    for(int b = 0;b < 3;b++){
                        for(int c = 0;c < 3;c++){
                            if(nums[i][a].second != nums[j][b].second
                             && nums[j][b].second != nums[k][c].second
                             && nums[i][a].second != nums[k][c].second){
                                ans = max(ans,nums[i][a].first + nums[j][b].first 
                                + nums[k][c].first);

                            }
                        }
                    }
                }
            }
        }
    }
    return ans;
}
};
