class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        vector<vector<pair<long long, int>>> v(a.size(), vector<pair<long long,int>>(a[0].size()));
        int n=a.size();
        int m=a[0].size();
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                v[i][j]={a[i][j], j};
            }
            sort(v[i].begin(), v[i].end());
            reverse(v[i].begin(), v[i].end());
        }
        long long ans=-1e18;
        for(int r1=0;r1<n;r1++){
            for(int r2=r1+1;r2<n;r2++){
                for(int r3=r2+1;r3<n;r3++){
                    for(int c1=0;c1<min(3,m);c1++){
                        for(int c2=0;c2<min(3,m);c2++){
                            for(int c3=0;c3<min(3,m);c3++){
                                if(v[r1][c1].second==v[r2][c2].second||v[r3][c3].second==v[r2][c2].second||v[r1][c1].second==v[r3][c3].second)continue;
                                ans=max(ans,v[r1][c1].first+v[r2][c2].first+v[r3][c3].first);
                            }
                        }   
                    }
                }
            }
        }
        return ans;
    }
};
