class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        using ll = long long;
        int n = v.size() , m = v[0].size();
        vector<vector<int>> up(n , vector<int> (m , -2e9)) , down(n , vector<int> (m));
        for(int i = 0 ; i < n ; i++){
            for(int j = 0 ; j < m ; j++){
                up[i][j] = v[i][j] ;
                if(i)up[i][j] = max(up[i][j] , up[i-1][j]);

            }
        }
        for(int i = n-1;i>=0;i--){
            for(int j = 0 ; j < m ; j++){
                down[i][j] = v[i][j];
                if(i + 1 < n)down[i][j] = max(down[i][j] , down[i+1][j]);
            }
        }
        ll ans = -1e18;
        for(int i = 1 ; i < n-1; i++ ){
            for(int j = 0 ; j < m ; j++){
                vector<ll> p(m , -1e18) , p2(m , -1e18);
                for(int c = 0 ; c < m ; c++){
                    if(c != j){
                        p[c] = up[i-1][c];
                        p2[c] = down[i+1][c];
                    }
                    if(c)p[c] = max<ll>(p[c] , p[c-1]) , p2[c] = max<ll>(p2[c] , p2[c-1]);
                }
                ll val = -1e18;
                ll val2 = -1e18;
                for(int c = m-1 ; c >= 0 ; c--){
                      if(c != j){
                          val = max<ll>(val , down[i+1][c]);
                          val2 = max<ll>(val2 , up[i-1][c]);
                      }
                    if(c-1>=0)ans = max<ll>(ans , val + v[i][j] + p[c-1]) , ans = max<ll>(ans , val2 + v[i][j] + p2[c-1]);
                }
            }
        }
        return ans;
    }
};
