class Solution:
    def maximumValueSum(self, A: List[List[int]]) -> int:
        B = [nlargest(3, ((x, i) for i, x in enumerate(row))) for row in A]
        @cache
        def dp(i, cc):
            if len(cc) == 3:
                return 0
            if i == len(B):
                return -inf
            res = dp(i + 1, cc)
            for b, j in B[i]:
                if j not in cc:
                    res = max(res, b + dp(i + 1, cc | {j}))
            return res
        return dp(0, frozenset())
