class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size(),n=board[0].size();
        vector<vector<pair<int,int>>> mmax(m);
        for(int i=0;i<m;i++){
            priority_queue<pair<int,int>> pq;
            for(int j=0;j<n;j++){
                pq.push({board[i][j],j});
            }
            for(int j=0;j<3;j++){
                mmax[i].push_back({pq.top()});
                pq.pop();
            }
        }
        // for(int i=0;i<m;i++){
        //     for(int j=0;j<3;j++){
        //         cout<<mmax[i][j].first<<" "<<mmax[i][j].second<<" ";
        //     }cout<<endl;
        // }
        long long ans=LONG_LONG_MIN;
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                for(int l=j+1;l<m;l++){
                    for(int i1=0;i1<3;i1++){
                        for(int i2=0;i2<3;i2++){
                            for(int i3=0;i3<3;i3++){
                                if(mmax[i][i1].second==mmax[j][i2].second||mmax[i][i1].second==mmax[l][i3].second||mmax[j][i2].second==mmax[l][i3].second){
                                    continue;        
                                }
                                ans=max(ans,1ll*mmax[i][i1].first+1ll*mmax[j][i2].first+1ll*mmax[l][i3].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
