class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])

        ghost = []

        for i in range(m):
            grow = []
            for j in range(n):
                grow.append([board[i][j], j])
            ghost.append(sorted(grow))

        maxSum = -10**10

        def getLargestSum(r1, r2, r3):
            if r1[-1][1] != r2[-1][1] and r1[-1][1] != r3[-1][1] and r2[-1][1] != r3[-1][1]:
                return r1[-1][0] + r2[-1][0] + r3[-1][0]
            elif r1[-1][1] == r2[-1][1] and r1[-1][1] == r3[-1][1]:
                return max(getLargestSum(r1[:-1], r2, r3), getLargestSum(r1, r2[:-1], r3), getLargestSum(r1, r2, r3[:-1]))
            elif r1[-1][1] == r2[-1][1]:
                return max(getLargestSum(r1[:-1], r2, r3), getLargestSum(r1, r2[:-1], r3))
            elif r1[-1][1] == r3[-1][1]:
                return max(getLargestSum(r1[:-1], r2, r3), getLargestSum(r1, r2, r3[:-1]))
            else:
                return max(getLargestSum(r1, r2[:-1], r3), getLargestSum(r1, r2, r3[:-1]))

        for i in range(m):
            for j in range(i):
                for k in range(j):
                    if i != j and j != k:
                        maxSum = max(maxSum, getLargestSum(ghost[i], ghost[j], ghost[k]))

        return maxSum
