using i64 = long long;

const i64 INF = 4000000000LL;

class Solution {
public:
    i64 maximumValueSum(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        auto gao = [&](int id) {
            vector<int> d(3, -1);
            for (int j = 0; j < m; j++) {
                int x = g[id][j];
                if (d[0] == -1 || x > g[id][d[0]]) d[2] = d[1], d[1] = d[0], d[0] = j;
                else if (d[1] == -1 || x > g[id][d[1]]) d[2] = d[1], d[1] = j;
                else if (d[2] == -1 || x > g[id][d[2]]) d[2] = j;
            }
            return d;
        };
        vector<vector<int>> a(n);
        for (int i = 0; i < n; i++) a[i] = gao(i);
        i64 ans = -INF;
        for (int i = 0; i < n; i++) for (int j = i + 1; j < n; j++) for (int k = j + 1; k < n; k++) {
            for (int x = 0; x < 3; x++) for (int y = 0; y < 3; y++) for (int z = 0; z < 3; z++) {
                if (a[i][x] == a[j][y] || a[i][x] == a[k][z] || a[j][y] == a[k][z]) continue;
                i64 tmp = (i64) g[i][a[i][x]] + g[j][a[j][y]] + g[k][a[k][z]];
                ans = max(ans, tmp);
            }
        }
        return ans;
    }
};
