class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n = len(board)
        m = len(board[0])

        cells = []
        for i in range(n):
            for j in range(m):
                cells.append((board[i][j], i, j))

        cells.sort(reverse=True)
        print(cells)

        x = len(cells) # 10^4
        limit = 110

        best = -10**30

        c1_count = 0
        for c1_val, c1_r, c1_c in cells:
            c1_count += 1
            if c1_count == limit:
                break

            c2_count = 0
            for c2_val, c2_r, c2_c in cells:
                if c2_r == c1_r or c2_c == c1_c:
                    continue

                c2_count += 1
                if c2_count == limit:
                    break

                for c3_val, c3_r, c3_c in cells:
                    if c3_r == c1_r or c3_r == c2_r or c3_c == c1_c or c3_c == c2_c:
                        continue

                    best = max(best, c1_val + c2_val + c3_val)
                    break

        return best


