class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int a = board.size();
        int b = board[0].size();
        long long INF = 1000000000;
        long long ans = -INF - INF - INF;
        vector <pair<int, pair<int,int>>> q;
        for (int i=0;i<a;i++) {
            for (int j=0;j<b;j++) {
                q.push_back({board[i][j], {i,j}});
            }
        }
        sort(q.begin(), q.end());
        for (int i=3;i<q.size();i++) {
            int x1 = -1;
            int y1 = -1;
            int x2 = -1;
            int y2 = -1;
            int x3 = -1;
            int y3 = -1;
            int x4 = -1;
            int y4 = -1;
            int x5 = -1; 
            int y5 = -1; 
            int x6 = -1;
            int y6 = -1;
            for (int cur = i-1;cur>=0;cur--) {
                if (x1 == -1) {
                    if (q[cur].second.first!=q[i].second.first && q[cur].second.second!=q[i].second.second) {
                        x1 = q[cur].second.first; y1 = q[cur].second.second;
                    }
                } else {
                    if (x2==-1 &&  q[cur].second.first!=q[i].second.first && q[cur].second.second!=q[i].second.second && q[cur].second.first!=x1 && q[cur].second.second!=y1) {
                        x2 = q[cur].second.first; y2 = q[cur].second.second;
                }
                    if (x3 == -1) {
                        if (q[cur].second.first!=q[i].second.first && q[cur].second.second!=q[i].second.second) {
                            x3 = q[cur].second.first; y3 = q[cur].second.second;
                        }
                    } else {
                        if (q[cur].second.first!=q[i].second.first && q[cur].second.second!=q[i].second.second) {
                            x5 = q[cur].second.first; y5 = q[cur].second.second;
                        }
                    }
                }


                if (x3!=-1 && x4==-1) {
                    if (q[cur].second.first!=q[i].second.first && q[cur].second.second!=q[i].second.second && q[cur].second.first!=x3 && q[cur].second.second!=y3) {
                    x4 = q[cur].second.first; y4 = q[cur].second.second;
                }
                }
                if (x5!=-1 && x6==-1) {
                    if (q[cur].second.first!=q[i].second.first && q[cur].second.second!=q[i].second.second && q[cur].second.first!=x5 && q[cur].second.second!=y5) {
                    x6 = q[cur].second.first; y6 = q[cur].second.second; break;
                }
                } 
            }

            if (x1!=-1 && x2!=-1)
            ans = max(ans, (long long) board[q[i].second.first][q[i].second.second] + board[x1][y1] + board[x2][y2]);
            if (x3!=-1 && x4!=-1)
            ans = max(ans, (long long) board[q[i].second.first][q[i].second.second] + board[x3][y3] + board[x4][y4]);
            if (x5!=-1 && x6!=-1)
            ans = max(ans, (long long) board[q[i].second.first][q[i].second.second] + board[x5][y5] + board[x6][y6]);
        }
        return ans;
    }
};
