class Solution {
public:
    typedef long long ll;
    typedef pair<int,int> pii;
    // int m;
    // unordered_set<int> st;
    ll solve(int ind,int prev1,int prev2,int num,vector<vector<pii>>& arr,vector<vector<vector<ll>>> &dp){
        if(num==0) return 0;
        if(ind>=arr.size()){
            return -4e9;
        }
        if(dp[ind][prev1+1][prev2+1]!=-1){
            return dp[ind][prev1+1][prev2+1];
        }
        ll ans = -4e9;
        for(int i=0;i<3;i++){
            if(arr[ind][i].second!=prev1 && arr[ind][i].second!=prev2){
                if(prev1==-1){
                    ans = max(ans,arr[ind][i].first + solve(ind+1,arr[ind][i].second,prev2,num-1,arr,dp));
                }else{
                    ans = max(ans,arr[ind][i].first + solve(ind+1,prev1,arr[ind][i].second,num-1,arr,dp));
                }
            }
        }
        ans = max(ans,solve(ind+1,prev1,prev2,num,arr,dp));
        return dp[ind][prev1+1][prev2+1] = ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<vector<ll>>> dp(n,vector<vector<ll>>(m+1,vector<ll>(m+1,-1)));
        vector<vector<pii>> arr(n);
        priority_queue<pii,vector<pii>,greater<pii>> pq;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                pq.push({board[i][j],j});
                if(pq.size()>3){
                    pq.pop();
                }
            }
            while(!pq.empty()){
                auto top = pq.top();
                pq.pop();
                arr[i].push_back({top.first,top.second});
            }
        }
        return solve(0,-1,-1,3,arr,dp);
    }
};
