class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        coordinates = list()
        n = len(board)
        m = len(board[0])
        for i in range(n):
            for j in range(m):
                coordinates.append((board[i][j], i, j))
        coordinates.sort()
        coordinates.reverse()

        o = len(coordinates)
        answer = -float('inf')
        for i in range(o):
            value_i, x_i, y_i = coordinates[i]
            for j in range(i+1, o):
                value_j, x_j, y_j = coordinates[j]
                if j+1 < o and value_i + value_j + coordinates[j+1][0] < answer:
                    # greedy break
                    break
                for k in range(j+1, o):
                    value_k, x_k, y_k = coordinates[k]
                    if x_i != x_j and x_j != x_k and x_i != x_k and y_i != y_j and y_j != y_k and y_i != y_k and \
                            (calculate := value_i + value_j + value_k) > answer:
                        answer = calculate

        return answer
