class Solution {
public:
    ll maximumValueSum(vector<vector<int>>& board) {
        int m = sz(board);
        int n = sz(board[0]);

        vector<vector<pi>> top3(n, vector<pi>(3, {INT_MIN, -1}));

        // Store top 3 values for each column
        forn(j, n) {
            forn(i, m) {
                if (board[i][j] > top3[j][0].F) {
                    top3[j][2] = top3[j][1];
                    top3[j][1] = top3[j][0];
                    top3[j][0] = {board[i][j], i};
                } else if (board[i][j] > top3[j][1].F) {
                    top3[j][2] = top3[j][1];
                    top3[j][1] = {board[i][j], i};
                } else if (board[i][j] > top3[j][2].F) {
                    top3[j][2] = {board[i][j], i};
                }
            }
        }

        ll maxSum = LLONG_MIN;

        // Try each column as the middle column
        fr(mid, 1, n - 2) {
            forn(left, mid) {
                fr(right, mid + 1, n - 1) {
                    forn(l, 3) {
                        forn(m, 3) {
                            forn(r, 3) {
                                if (top3[left][l].S != top3[mid][m].S &&
                                    top3[mid][m].S != top3[right][r].S &&
                                    top3[left][l].S != top3[right][r].S) {
                                    ll sum = (ll)top3[left][l].F + 
                                             (ll)top3[mid][m].F + 
                                             (ll)top3[right][r].F;
                                    maxSum = max(maxSum, sum);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
