#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<ll>>Maxi(m, vector<ll>(3));
        for(int i = 0; i < m; i++){
            vector<pair<int,int>>v;
            for(int j = 0; j < n; j++){
                v.push_back({board[i][j], j});
            }
            sort(rbegin(v), rend(v));
            for(int j = 0; j < 3; j++){
                Maxi[i][j] = v[j].second;
            }
        }
        ll ans = LLONG_MIN;
        for(int r1 = 0; r1 < m; r1++){
            for(int r2 = r1 + 1; r2 < m; r2++){
                for(int r3 = r2 + 1; r3 < m; r3++){
                    for(int i = 0; i < 3; i++){
                        for(int j = 0; j < 3; j++){
                            for(int k = 0; k < 3; k++){
                                if(Maxi[r1][i] != Maxi[r2][j] && Maxi[r1][i] != Maxi[r3][k] && Maxi[r3][k] != Maxi[r2][j])
                                ans = max(ans, board[r1][Maxi[r1][i]]*1LL + board[r2][Maxi[r2][j]]*1LL + board[r3][Maxi[r3][k]]*1LL);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
