class Solution {
public:

    long long get(vector<pair<int,int>>& a,vector<pair<int,int>>& b,vector<pair<int,int>> & c){
        long long ans = -1ll << 50;
        for(int i = 0;i < 3;++i){
            for(int j = 0;j < 3;++j){
                if(a[i].second == b[j].second)continue;
                for(int k = 0;k < 3;++k){
                    if(a[i].second == c[k].second || b[j].second == c[k].second)continue;
                    ans = max(ans, 1ll * a[i].first + b[j].first + c[k].first);
                    //cout << 
                }
            }
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<pair<int,int>>> m;
        int n = board.size();
        int end = board[0].size();
        for(int i = 0;i < n;++i){
            priority_queue<pair<int,int>> p;
            m.push_back({});
            for(int j = 0;j < end;++j){
                p.push({board[i][j], j});
            }
            m.back().push_back(p.top());
            p.pop();
            m.back().push_back(p.top());
            p.pop();
            m.back().push_back(p.top());
            p.pop();
        }
        long long ans = -1ll << 50;
        for(int i = 0;i < n;++i){
            for(int j = i + 1;j < n;++j){
                for(int k = j + 1;k < n;++k){
                    ans = max(ans, get(m[i],m[j],m[k]));
                }
            }
        }
        return ans;

    }
};
