class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        arr = []
        se = set()
        for i in range(m):
            temp = [(-board[i][j], i, j) for j in range(n)]
            temp.sort()
            for k in range(3):
                se.add((temp[k][1], temp[k][2]))
        for j in range(n):
            temp = [(-board[i][j], i, j) for i in range(m)]
            temp.sort()
            for k in range(3):
                if (temp[k][1], temp[k][2]) in se:
                    arr.append((temp[k][0], temp[k][1], temp[k][2]))
        arr.sort()

        res = -inf
        k = len(arr)
        for i in range(k-2):
            if res >= -arr[i][0] * 3:
                return res
            x1 = -arr[i][0]
            for j in range(i+1, k-1):
                if res >= x1 - arr[j][0] * 2:
                    break
                if arr[j][1] == arr[i][1] or arr[j][2] == arr[i][2]:
                    continue
                x2 = -arr[j][0]
                for t in range(j+1, k):
                    if res >= x1 + x2 - arr[t][0]:
                        break
                    if arr[t][1] == arr[i][1] or arr[t][2] == arr[i][2] or arr[t][1] == arr[j][1] or arr[t][2] == arr[j][2]:
                        continue
                    res = max(res, x1 + x2 - arr[t][0])
        return res
